class HomeRecipe {
  String _name;
  String _state;
  List<Recipe> _list;
  String _message;

  String get name => _name;
  String get state => _state;
  List<Recipe> get list => _list;
  String get message => _message;

  HomeRecipe({
      String name, 
      String state, 
      List<Recipe> list,
      String message}){
    _name = name;
    _state = state;
    _list = list;
    _message = message;
}

  HomeRecipe.fromJson(dynamic json) {
    _name = json["name"];
    _state = json["state"];
    if (json["list"] != null) {
      _list = [];
      json["list"].forEach((v) {
        _list.add(Recipe.fromJson(v));
      });
    }
    _message = json["message"];
  }

  Map<String, dynamic> toJson() {
    var map = <String, dynamic>{};
    map["name"] = _name;
    map["state"] = _state;
    if (_list != null) {
      map["list"] = _list.map((v) => v.toJson()).toList();
    }
    map["message"] = _message;
    return map;
  }

}

